class LinkedNode:
    def __init__(self, val):
        self.val=val
        self.next=None
class Solution:
    def findLength(self, A, B):
        """
        :type A: List[int]
        :type B: List[int]
        :rtype: int
        """
        asize=len(A)
        bsize=len(B)
        if asize ==0 or bsize ==0:
            return 0
        dp=[0]*(bsize+1)
        maxSofar=0
        for i in range(asize-1,-1,-1):
            for j in range(bsize):
                if A[i]==B[j]:
                    dp[j]=dp[j+1]+1
                else:
                    dp[j]=0
                maxSofar=max(maxSofar, dp[j])
        return maxSofar

